import React, { useEffect, useState } from 'react';
import Request from '../../../helpers/request';

const RecipeForm = ({ onCreate, onPreparedIngredient, ingredients }) => {

    const [meals, setMeals] = useState([]);
    const [tags, setTags] = useState([]);
    const [measurementTypes, setMeasurementTypes] = useState([]);

    const [recipe, setRecipe] = useState({});
    const [preparedIngredient, setPreparedIngredient] = useState({});
    const [preparedIngredientsList, setPreparedIngredientsList] = useState([]);

    const [ingredientToggle, setIngredientToggle] = useState(false);

    // fetch eNums from backend
    useEffect(() => {
        getMeals();
    }, [])

    useEffect(() => {
        getTags();
    }, [])

    useEffect(() => {
        getMeasurementTypes();
    }, [])

    const getMeals = () => {
        Request.get('http://localhost:8080/meal')
            .then(mealData => setMeals(mealData));
    }

    const getTags = () => {
        Request.get('http://localhost:8080/tags')
            .then(tagData => setTags(tagData));
    }

    const getMeasurementTypes = () => {
        Request.get('http://localhost:8080/measurements')
            .then(measurementData => setMeasurementTypes(measurementData));
    }


    // handle form events
    const handleChange = (event) => {
        let propertyName = event.target.name;
        let copiedRecipe = { ...recipe }
        copiedRecipe[propertyName] = event.target.value;
        setRecipe(copiedRecipe)
    }

    const handleMeal = function (event) {
        const index = parseInt(event.target.value)
        const selectedMeal = meals[index]
        let copiedRecipe = { ...recipe };
        copiedRecipe['meal'] = selectedMeal
        setRecipe(copiedRecipe)
    }

    const handleTags = function (event) {
        const index = parseInt(event.target.value)
        const selectedTag = tags[index]
        let copiedRecipe = { ...recipe };
        copiedRecipe['tags'] = selectedTag
        setRecipe(copiedRecipe)
    }

    // submitting prepared ingredients
    const handlePreparedIngredientChange = (event) => {
        let propertyName = event.target.name;
        let copiedPreparedIngredient = { ...preparedIngredient };
        copiedPreparedIngredient[propertyName] = event.target.value;
        setPreparedIngredient(copiedPreparedIngredient);
    }

    const handleIngredients = function (event) {
        const index = parseInt(event.target.value)
        const selectedIngredient = ingredients[index]
        let copiedPreparedIngredient = { ...preparedIngredient };
        copiedPreparedIngredient['ingredient'] = selectedIngredient
        setPreparedIngredient(copiedPreparedIngredient)
    }

    const handleMeasurementType = function (event) {
        const index = parseInt(event.target.value)
        const selectedMeasurementType = measurementTypes[index]
        let copiedPreparedIngredient = { ...preparedIngredient };
        copiedPreparedIngredient['measurementType'] = selectedMeasurementType
        setPreparedIngredient(copiedPreparedIngredient)
    }

    const handlePreparedIngredientSubmit = function (event) {
        event.preventDefault();
        onPreparedIngredient(preparedIngredient);
        setPreparedIngredientsList([...preparedIngredient]);
    }

    // add more ingredients
    const addAnotherIngredient = function () {
        return (
            <>
                <select name="ingredient" onChange={handleIngredients} defaultValue="select-ingredient">
                <option disabled value="select-ingredient">Select an ingredient</option>
                        {ingredientsOptions}
                    </select>
                    <input type="number" placeholder="Measurement" name="measurement" onChange={handlePreparedIngredientChange} defaultValue={preparedIngredient.measurement} />
                    <select name="measurementType" onChange={handleMeasurementType} defaultValue="select-measurement-type">
                        <option disabled value="select-measurement-type">Select a measurement type</option>
                        {measurementTypeOptions}
                    </select>
                    <input type="text" placeholder="Preparation" name="preparation" onChange={handlePreparedIngredientChange} defaultValue={preparedIngredient.preparation} />
                    <button onClick={handlePreparedIngredientSubmit}>Save ingredient</button>
                    <br />
                <button onClick={addAnotherIngredient}>Add another ingredient</button>
            </>
        )
    }


    // create recipe
    const handleSubmit = function (event) {
        event.preventDefault();
        onCreate(recipe);
        setRecipe(recipe);
        setIngredientToggle(true);
    }


    const mealOptions = meals.map((meal, index) => {
        return <option key={index} value={index}>{meal}</option>
    })

    const tagsOptions = tags.map((tag, index) => {
        return <option key={index} value={index}>{tag}</option>
    })

    const ingredientsOptions = ingredients.map((ingredient, index) => {
        return <option key={index} value={index}>{ingredient.name}</option>
    })

    const measurementTypeOptions = measurementTypes.map((measurement_type, index) => {
        return <option key={index} value={index}>{measurement_type}</option>
    })



    return (
        <div className="recipe-form">
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Recipe name" name="name" onChange={handleChange} defaultValue={recipe.name} />
                <select name="type" onChange={handleMeal} defaultValue="select-meal">
                    <option disabled value="select-meal">Select a meal</option>
                    {mealOptions}
                </select>
                {/* <input type="number" placeholder="Numer of portions" name="portions" onChange={handleChange} defaultValue={recipe.portions} /> */}
                <input type="number" step="0.01" placeholder="Cooking time" name="cooking time" onChange={handleChange} defaultValue={recipe.cooking_time} />
                <button type="submit">Create Recipe</button>
            </form>

                <br />
                <br />

            {/* PREPARED INGREDIENTS */}

            {ingredientToggle ?
                <form>
                    <p>Add ingredient:</p>
                    <select name="ingredient" onChange={handleIngredients} defaultValue="select-ingredient">
                        <option disabled value="select-ingredient">Select an ingredient</option>
                        {ingredientsOptions}
                    </select>
                    <input type="number" placeholder="Measurement" name="measurement" onChange={handlePreparedIngredientChange} defaultValue={preparedIngredient.measurement} />
                    <select name="measurementType" onChange={handleMeasurementType} defaultValue="select-measurement-type">
                        <option disabled value="select-measurement-type">Select a measurement type</option>
                        {measurementTypeOptions}
                    </select>
                    <input type="text" placeholder="Preparation" name="preparation" onChange={handlePreparedIngredientChange} defaultValue={preparedIngredient.preparation} />
                    <button onClick={handlePreparedIngredientSubmit}>Save ingredient</button>
                    <br />
                    <button onClick={addAnotherIngredient}>Add another ingredient</button>
                    <br />
                    <button onClick={handlePreparedIngredientSubmit}>Submit ingredients</button>
                </form>
                : <></>}


                {/* TAGS */}
                <br />
                <br />
                <p>Add tag:</p>
                <select name="tags" onChange={handleTags} defaultValue="select-tag">
                    <option disabled value="select-tag">Select a tag</option>
                    {tagsOptions}
                </select>

                {/* INSTRUCTIONS */}
                <br />
                <br />
                <p>Add instruction:</p>
                <input type="text" placeholder="Instructions" name="instructions" onChange={handleChange} defaultValue={recipe.instructions} />

                <br />
                <br />


        </div>
    )
}

export default RecipeForm;
