import React, {useState} from 'react';

const IngredientForm = ({ onCreate }) => {

    const [ingredient, setIngredient] = useState({
        name: "",
        type: "",
        calories: 0,
        measurement: 0,
        measurement_type: ""
    });

    const handleChange = function (event) {
        let propertyName = event.target.name;
        let copiedIngredient = { ...ingredient }
        copiedIngredient[propertyName] = event.target.value;
        setIngredient(copiedIngredient)
    }

    const handleType = function (event) {
        const index = parseInt(event.target.value)
        const selectedType = types[index]
        let copiedIngredient = { ...ingredient };
        copiedIngredient['type'] = selectedType
        setIngredient(copiedIngredient)
    }

    const handleMeasurementType = function (event) {
        const index = parseInt(event.target.value)
        const selectedShip = measurementTypes[index]
        let copiedPirate = { ...ingredient };
        copiedIngredient['measurementType'] = selectedMeasurementType
        setIngredient(copiedIngredient)
    }

    const handleSubmit = function (event) {
        console.log("handle submit called");
        event.preventDefault();
        onCreate(statePirate);
    }

    return (
        <div className="ingredient-form">
            <form onSubmit={handleSubmit}>
                <input type="text" placeholder="Ingredient Name" name="Name" onChange={handleChange} value={ingredient.name} />
                <select name="Type" onChange={handleType} defaultValue="select-type">
                    <option disabled value="select-type"> Select an ingredient type</option>
                        {typeOptions}
                </select>
                <input type="number" placeholder="Number of Calories" name="Calories" onChange={handleChange} value={ingredient.calories} />
                <input type="number" placeholder="Measurement" name="per" onChange={handleChange} value={ingredient.measurement} />
                <select name="Measurement Type" onChange={handleMeasurementType} defaultValue="select-measurement-type">
                    <option disabled value="select-measurement-type"> Select a measurement type</option>
                    {measurementTypeOptions}
                </select>
                <button type="submit">Save</button>

                <select name="ship" onChange={handleShip} defaultValue="select-ship">
                    <option disabled value='select-ship'>Select a ship</option>
                    {shipOptions}
                </select>
            </form>

        </div>
    )
}

export default IngredientForm;
