const recipeURL = 'localpi/recipes';

const Request = {

    get() {
        return fetch(recipeURL)
            .then(res => res.json());
    },

    post(url, payload) {
        return fetch(url, {
            method: "POST",
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(payload)
        })
    },

    delete(url) {
        return fetch(url, {
            method: "DELETE",
            headers: { 'Content-Type': 'application/json' }
        })
    }
}




export default Request;
